import styled from "styled-components";

export const Hire = styled.div`
    margin-top: 50px;
    margin-bottom: 50px;
    width: 100%;
    background-color: #787D89;
    padding: 50px 40px;
    border-radius: 10px;
    color: white;
    display: flex;
    justify-content: space-between;

    button{
        border: none;
        background: var(--btn-color);
        color: white;
        font-weight: bold;
        border-radius: 28px;
        padding: 8px 16px;
    }

    @media (max-width: 480px) {
        padding: 25px 20px;
        flex-direction
    }
`;
