import styled from 'styled-components'

export const ProjectList = styled.div`
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    grid-gap: 20px;
    align-items: center;
    justify-content: center;

    @media(max-width: 480px)  {
        grid-template-columns: 1fr;
        
    }

    .projectImg{
        height: 180px;
    }
`;
